// DATASET: cakes from ./datasets/cakes
const cakePrompts = {
  stockPerCake() {
    // Return an array of objects that include just the flavor of the cake and how
    // much of that cake is in stock e.g.
    // [ 
    //    { flavor: 'dark chocolate', inStock: 15 },
    //    { flavor: 'yellow', inStock: 14 },
    //    ..etc
    // ]

    const result = cakes.map(obj => ({
      flavor: obj.cakeFlavor, 
      inStock: obj.inStock
    }));
    return result;

    // Annotation:
    // We set result's value to a mapping of cakes that returns the flavor and stock status with the new keys.
  },

  onlyInStock() {
    // Return an array of only the cakes that are in stock
    // e.g.
    // [
    //   {
    //   cakeFlavor: 'dark chocolate',
    //   filling: null,
    //   frosting: 'dark chocolate ganache',
    //   toppings: ['dutch process cocoa', 'toasted sugar', 'smoked sea salt'],
    //   inStock: 15
    // },
    // {
    //   cakeFlavor: 'yellow',
    //   filling: 'citrus glaze',
    //   frosting: 'chantilly cream',
    //   toppings: ['berries', 'edible flowers'],
    //   inStock: 14
    // },
    // ..etc
    // ]

    const result = cakes.filter(obj => obj.inStock > 0);
    return result;

    // Annotation:
    // We set the result to a filter of only objects where the stock is more than 0.
  },
  
  totalInventory() {
    // Return the total amount of cakes in stock e.g.
    // 59

    const result = cakes.reduce((sum, obj) => sum + obj.inStock, 0);
    return result;

    // Annotation:
    // We use reduce to get a total of numbers in the objects' inStock properties.
  },

  allToppings() {
    // Return an array of all unique toppings (no duplicates) needed to bake
    // every cake in the dataset e.g.
    // ['dutch process cocoa', 'toasted sugar', 'smoked sea salt', 'berries', ..etc]
    const toppingSet = new Set(cakes.map(cake => cake.toppings).flat(1));

    const result = Array.from(toppingSet);
    return result;

    // Annotation:
    // Write your annotation here as a comment
  },

  groceryList() {
    // I need to make a grocery list. Please give me an object where the keys are
    // each topping, and the values are the amount of that topping I need to buy e.g.
    // { 
    //    'dutch process cocoa': 1,
    //    'toasted sugar': 3,
    //    'smoked sea salt': 3,
    //    'berries': 2, 
    //    ...etc
    // }
    let toppings = cakes.map(cake => cake.toppings).flat(1);
    let counts = {};
    const newList = {};
    
    toppings.forEach(top => counts[top] = counts[top] ? counts[top] + 1 : 1);
    
    toppings = Array.from(new Set(toppings));

    toppings.forEach((top, i) => {
        newList[toppings[i]] = counts[top];
    });

    const result = newList;
    return result;

    // Annotation:
    // We first set variables for toppings (a flat array of all toppings), counts, and newList (empty objects for now)
    // Then, we loop through each topping and count how many duplicates there are and store that
    // Then, we convert toppings to a list with no duplicates, and loop through to push new key/value pairs with topping and count
  }
};






