// DATASET: classrooms from ./datasets/classrooms
const classPrompts = {
  feClassrooms() {
    // Create an array of just the front-end classrooms. e.g.
    // [
    //   { roomLetter: 'A', program: 'FE', capacity: 32 },
    //   { roomLetter: 'C', program: 'FE', capacity: 27 },
    //   { roomLetter: 'E', program: 'FE', capacity: 22 },
    //   { roomLetter: 'G', program: 'FE', capacity: 29 }
    // ]



    const result = classrooms.filter(room => room.program === 'FE');
    return result;

    // Annotation:
    // We filter to only the objects with a value of 'FE' for the program prop
  },

  totalCapacities() {
    // Create an object where the keys are 'feCapacity' and 'beCapacity',
    // and the values are the total capacity for all classrooms in each program e.g.
    // { 
    //   feCapacity: 110,
    //   beCapacity: 96
    // }

    let feCap = classrooms.filter(obj => obj.program === 'FE').reduce((sum, obj) => {
        sum += obj.capacity;
        return sum;
      }, 0);

      let beCap = classrooms.filter(obj => obj.program === 'BE').reduce((sum, obj) => {
        sum += obj.capacity;
        return sum;
      }, 0);

    const result = {
      feCapacity: feCap,
      beCapacity: beCap
    };

    return result;

    // Annotation:
    // Write your annotation here as a comment
  },

  sortByCapacity() {
    // Return the array of classrooms sorted by their capacity (least capacity to greatest)

    const result = classrooms.sort((a, b) => a.capacity - b.capacity);
    return result;

    // Annotation:
    // Write your annotation here as a comment
  }
};






